import React, { Component } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Timeline from "@material-ui/lab/Timeline";
import TimelineItem from "@material-ui/lab/TimelineItem";
import TimelineSeparator from "@material-ui/lab/TimelineSeparator";
import TimelineConnector from "@material-ui/lab/TimelineConnector";
import TimelineContent from "@material-ui/lab/TimelineContent";
import TimelineOppositeContent from "@material-ui/lab/TimelineOppositeContent";
import TimelineDot from "@material-ui/lab/TimelineDot";

import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";

import { PlayArrow, CheckCircle, Stop } from "@material-ui/icons";

const useStyles = makeStyles((theme) => ({
  paper: {
    padding: "6px 16px",
  },
  secondaryTail: {
    backgroundColor: theme.palette.secondary.main,
  },
  paper: {
    backgroundColor: "#fff",
    padding: theme.spacing(3),
    boxShadow: "0px 1px 4px #0000001A",
  },
  timeline: {
    transform: "rotate(-90deg)",
    [theme.breakpoints.down("sm")]: {
      transform: "rotate(0deg)",
    },
  },
  timelineContentContainer: {
    textAlign: "left",
  },
  timelineContent: {
    display: "inline-block",
    transform: "rotate(40deg)",
    textAlign: "center",
    minWidth: 50,
    padding: 10,
  },
  title: {
    // transform: "rotate(90deg)"
  },
  timelineIcon: {
    transform: "rotate(90deg)",
    [theme.breakpoints.down("md")]: {
      transform: "rotate(0deg)",
    },
    color: theme.palette.success.main,
  },
}));

export const timelineData = [
  {
    task: "Repave Ready",
    tasktime: "30-03-2021 17:05:06 PM",
  },
  {
    task: "Repave In progress",
    tasktime: "30-03-2021 17:05:06 PM",
  },
  {
    task: "DC Demo in Progress",
    tasktime: "30-03-2021 17:05:06 PM",
  },
  {
    task: "Repave Ready",
    tasktime: "30-03-2021 17:05:06 PM",
  },
  {
    task: "Repave Ready",
    tasktime: "30-03-2021 17:05:06 PM",
  },
  {
    task: "Repave Ready",
    tasktime: "30-03-2021 17:05:06 PM",
  },
  {
    task: "Repave Ready",
    tasktime: "30-03-2021 17:05:06 PM",
  },
];

export default function CustomizedTimeline() {
  const classes = useStyles();

  return (
    <Timeline align="alternate">
      <TimelineItem>
        <TimelineOppositeContent />

        <TimelineSeparator>
          <TimelineDot style={{ color: "#fff", backgroundColor: "#1B5E20" }}>
            <PlayArrow />
          </TimelineDot>
          <TimelineConnector />
        </TimelineSeparator>
        <TimelineContent></TimelineContent>
      </TimelineItem>
      {timelineData.map((item) => (
        <TimelineItem>
          <TimelineOppositeContent>
            <Typography variant="body2" color="textSecondary">
              {item.tasktime}
            </Typography>
          </TimelineOppositeContent>
          <TimelineSeparator>
            <CheckCircle
              style={{ color: "#00C853", backgroundColor: "#fff" }}
            />
            <TimelineConnector />
          </TimelineSeparator>
          <TimelineContent>
            <Paper elevation={3} className={classes.paper}>
              <Typography>{item.task}</Typography>
            </Paper>
          </TimelineContent>
        </TimelineItem>
      ))}
      <TimelineItem>
        <TimelineSeparator>
          <TimelineDot color="secondary">
            <Stop />
          </TimelineDot>
        </TimelineSeparator>
        <TimelineContent />
      </TimelineItem>
    </Timeline>
  );
}
